  `date` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=9 ;

--
-- Дамп данных таблицы `guestbook`
--

INSERT INTO `guestbook` (`id`, `name`, `email`, `text`, `date`) VALUES
(1, 'Ассиди', 'assidi@mail.ru', 'Первая запись в гостевой книге. Вот и проверим, как оно работает.', 1488959236),
(2, 'Ассиди', 'assidi@mail.ru', 'Ну вот еще что-нибудь напишем. Пока я залогинена, могу писать, что угодно, а для гостей пока еще не сделала эту возможность. И надо будет капчу еще приспособить.', 1489000549),
(3, 'Ассиди', 'assidi@mail.ru', 'Эту запись я делаю от гостя.', 1489155895),
(4, 'Ассиди', 'assidi@mail.ru', 'Вот сейчас и посмотрим, куда меня переадресует...', 1489155983),
(8, 'Ассиди', 'assidi@mail.ru', 'Ну что, гостевая работает, капча работает, если писать от гостя. По-хорошему, надо еще, если я залогинена, сразу писать мое имя и емейл... но пока что есть еще более актуальные изменения.  ', 1490117677);

