--
-- Структура таблицы `jokes`
--

CREATE TABLE IF NOT EXISTS `jokes` (
  `jokeId` int(11) NOT NULL AUTO_INCREMENT,
  `jokeText` varchar(800) NOT NULL,
  PRIMARY KEY (`jokeId`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=92 ;

--
-- Дамп данных таблицы `jokes`
--

INSERT INTO `jokes` (`jokeId`, `jokeText`) VALUES
(1, 'Миссис Хепзиба Смит демонстрирует Тому Риддлу свои сокровища: \n— Этот кубок принадлежал самой Хельге Хаффлпаф и я не продам его, даже если ты будешь угрожать меня изнасиловать! \n— Но я вовсе не собираюсь вас насиловать! \n— А придется!'),
(2, 'На уроке зельеварения Малфой говорит Гарри: \r\n— Поттер, нарежь мне корень имбиря! \r\n— А волшебное слово? \r\n— Империо!'),
(3, 'Люпин выговаривает Сириусу: \n— Сколько раз тебе говорили — будь осторожнее! Тебя и так в Лондоне каждая собака знает!'),
(4, 'Мистер Уизли сетует на то, что в Министерстве Магии, похоже, завелись \r\nприспешники Волдеморта, и не понятно как их вычислить. \r\n— Мы знаем, как тебе помочь, — говорят Фред и Джордж. \r\n— Чем вы можете помочь? — удивляется Артур Уизли. \r\n— У вас завтра заместитель министра Амбридж выступает с речью? Возьми нас с \r\nсобой. А мы там тебе всех и покажем. \r\nМистер Уизли удивился, но сыновей с собой взял. После доклада все начали \r\nрасходиться, Фред и Джордж ему показывают: \r\n— Вот тот, и вот тот. И еще вот этот. \r\n— Но как вы догадались? \r\n— Враг не дремлет!'),
(5, 'На перемене Гарри жалуется Рону и Гермионе: \r\n— У меня опять заболел шрам. К чему бы это? \r\nРон: Да пес его знает! \r\nГермиона: Правильно Рон говорит, тебе надо обязательно посоветоваться с Сириусом!'),
(6, 'Дамблдор представляет Грюма Грозного Глаза: \n— А это наш новый преподаватель по защите от Темных Искусств, профессор Грюм. Он на защите от темных заклятий собаку съел... Гарри, не дергайся ты так, цел твой Сириус, цел!'),
(7, 'После проигранного матча по квиддичу Гарри в больнице говорит Рону: \r\n— Какая жалость, что МакЛаген не стал Пожирателем смерти! \r\n— Почему это? — непонимающе спрашивает Рон?\r\n— Он сначала бы пришиб Волдеморта, демонстрируя ему, как правильно применять «Авада Кедавра», а потом бы его прикончили остальные Пожиратели Смерти.'),
(8, 'Размышления Снейпа: «Хорошо быть двойным агентом! Волдеморту сказать, что я выполняю задание Дамблдора, Дамблдору — что слежу за Волдемортом. А на самом деле — прийти в свой дом в магловском квартале и спать! Ибо достала уже эта война!».'),
(9, 'Просыпается утром Снейп — состояние как после Круциатуса, голова раскалывается, перед глазами зеленые искры... Лихорадочно соображает — так, если сюда войдут авроры, я работаю на Дамблдора и по его приказы шпионю за Волдемортом. А если войдут Упивающиеся Смертью, то я по приказу Волдеморта шпионю за Дамблдором. \r\nДверь открывается и входит ассистент режиссера: \r\n— Ну и надрались же вы вчера, мистер Рикман!'),
(10, 'Идет Артур Уизли по Министерству Магии, видит — идет ему навстречу Мундугнус Флетчер, в руке котел, довольно улыбается — ну не иначе как что-то спер. Артур его остановил, велел показать, что в котле, забрал все ингредиенты для зелий, что там были и прогнал Флетчера восвояси. И все равно еще не оставляла мысль, что Флетчер все-таки что-то да спер. \r\nЧеред несколько дней встречаются они на собрании Ордена Феникса и Артур говорит: \r\n— Мундугнус, скажи, что ты все-таки спер в Министерстве Магии? Дальше меня не пойдет, честное слово! \r\n— Как это что? — ухмыльнулся Флетчер. — Котел я там спер!'),
(11, 'Благополучно уйдя от дементоров, Сириус воскликнул: «Ай да Сириус, ай да сукин сын!».'),
(12, 'Из дневника поттероманки (2005 год): \r\n12 июля. Читала фанфик про Рона и Гермиону. Три часа плакала. \r\n13 июля. Читала фанфик про Малфоя. Полдня плакала. \r\n14 июля. Читала фанфик про детство Волдеморта. Плакала весь день. \r\n15 июля. Читала фанфик про Снейпа. Плакала весь день и всю ночь. Чуть не умерла от слез. \r\n16 июля. Купила шестую книгу. Читаю... \r\n17 июля. Лучше б я в пятницу умерла!'),
(13, 'Врывается Гарри к Дамблдору, страшно взволнованный, растрепанный, глаза горят... \r\n— Профессор Дамблдор! Я точно знаю — профессор Снейп на самом деле работает на Волдеморта! Я случайно подслушал его разговор с Малфоем. Надо немедленно удалить его из Хогвартса, он только притворяется, что на нашей стороне! \r\nДамблдор, спокойно улыбаясь, смотрит на Гарри: \r\n— И что только студенты не придумают, чтобы у Снейпа не учиться...'),
(14, 'Приезжает Невилл Лонгботтом домой на каникулы, весь расстроенный. Встречает его бабушка: \r\n— Что ты такой печальный, внучек? \r\n— Да вот, опять от профессора Снейпа досталось. \r\n— Ну он всегда к тебе придирается, — говорит бабушка и гладит внука по голове. — Что на этот раз случилось? \r\n— Я зелье неправильно сварил, а он заставил меня его выпить... \r\n— Ну ничего внучек, ничего, дома отдохнешь.. — сказала миссис Лонгботтом и погладила Невилла по второй голове.'),
(15, 'Гарри идет по коридору седьмого этажа и натыкается на профессора Трелони. Та начинает вещать: \r\n— Нам грозит ужасное несчастье! Башня, пораженная молнией...\r\n— Профессор Трелони, ну сколько раз просили — не надо спойлеров!'),
(16, 'Гарри укусил оборотень. В больничное крыло приходит к нему Гермиона и утешает: \r\n— Ничего страшного, профессор Люпин давно уже так живет. Перед полнолунием пойдешь к Снейпу, он даст тебе зелье... \r\nГарри, перебивая: \r\n— Нет, к Снейпу я пойду в полнолуние! И безо всякого зелья!'),
(17, 'Дамблдор послал на задание Снейпа и Сириуса (последний для конспирации был в собачьем облике). Возвращаются они на площадь Гриммо 12, их спрашивают, как успехи. Снейп брезгливо отряхивается: \r\n— По мне блохи ползают!\r\nСириус: \r\n— Я больше с тобой никуда не пойду, раз у тебя блохи!'),
(18, 'Чем поттероманка отличается от обычной девушки? \r\nОбычная девушка мечтает о прекрасном принце. А поттероманка — о Принце-Полукровке.'),
(19, 'Фадж (в панике): На Гарри Поттера напали дементоры!\r\nДамблдор (зевая): Ваши дементоры, вы и спасайте!'),
(20, 'После пресс-конференции, посвященной выходу книги «Гарри Поттер и Принц Полукровка» к Джоан Роулинг подошла группа фэнов и попросила ее на два слова. Писательница быстро ретировалась, ибо знала, какими будут эти два слова...'),
(21, 'Гермиона беседует с миссис Уизли: \r\n— Не думаю, что от предписанных Министерством мер предосторожности будет какой-нибудь толк. Есть ли смысл задавать вопросы, ведь если Пожиратель Смерти принял обличье члена семьи, он и ответы может выведать! \r\n— Как раз наоборот! Теперь каждый день, когда Артур возвращается с работы, я спрашиваю, куда он спрятал заначку и он вынужден говорить правду!'),
(22, '— Сколько можно собирать анекдоты по «Гарри Поттеру»? Это же детская книжка! \r\n— Детская книжка? Этого анекдота я еще не слышал!'),
(23, '— Бартемиус Крауч-младший, вы обвиняетесь в том, что пытали авроров Фрэнка и Алису Лонгботтомов заклятием Круциатус!\r\n— Неправда, никаким круциатусом я их не пытал!\r\n— Отчего же тогда они сошли с ума?\r\n— Я просто сказал им, что в переводе Литвиновой их фамилия звучит как «Долгопупс».'),
(24, 'Опрос на поттероманском форуме: \r\n1. Кто ваш любимый персонаж? \r\n2. Почему именно Снейп?'),
(25, 'На конкурсе фанфиков было поставлено условие — чтобы в фике упоминалась древняя магия, Снейп, слэш и была загадка. Первое место занял фик: «Мерлин! — вскричал профессор Снейп. — Я беременный и неизвестно от кого!»'),
(26, 'На потустороннем вокзале Кингс-Кросс встречаются Дамблдор и Сириус. Сириус говорит: \r\n— Профессор, у меня две новости — хорошая и плохая. \r\n— Ну, начинай с плохой. \r\n— Роулинг сказала в интервью, что вы гей! \r\nДамблдор смотрит на Сириуса сквозь очки-половинки: \r\n— А какая хорошая? \r\n— А я — нет!'),
(27, 'Крик души Крауча-старшего:\r\n— Ну какой непонятливый этот Визенгамот! Я им такое обвинительное заключение подготовил, так вину обвиняемого расписал — сам поверил, а они все не верят и не верят!'),
(28, 'Колин Криви, просматривая снимки: Что это такое, зеркалку последней модели купил, а от эффекта красных глаз избавиться не могу!\r\nГарри Поттер: Ты Волдеморта не фотографируй — и избавишься!'),
(29, 'Вузовским учебникам для первого курса можно ставить рейтинг NC-17, а для пятого - NC-21, раньше их все равно никто читать не будет!'),
(30, 'Приходит Лютиэн к Тинголу и Мелиан и говорит:\n— Мама, папа, я должна сказать вам одну важную вещь. Я бере...\n— Что?\n— Я Берена люблю.'),
(31, 'На празднике в Тирионе, стараниями Мелькора, не иначе, в кубок с вином каждому из потомков Финве попала муха.\r\nФеанаро выплеснул вино вместе с мухой в лицо Нолофинвэ.\r\nМайтимо вино выпил, а муху подарил Финдекано.\r\nМакалаурэ вынул муху и, держа ее в руке, сочинил проникновенную поэму. Про вино он забыл.\r\nТьелкормо вино выпил, а муху скормил Хуану.\r\nКуруфинвэ вино выпил, а муху засунул за шиворот Карнистиро.\r\nКарнистиро выпил несколько глотков, а остальное выплеснул за шиворот Куруфинвэ. Уворачиваясь от брата, схватил кубок Макалаурэ и допил его.\r\nАмбаруссар вытащили мух из своих кубков и пустили их летать наперегонки. Пока они этим занимались, Куруфинвэ и Карнистиро выпили их вино.'),
(32, 'Выходит Мелькор из Мандоса после 300 лет заключения и спрашивает Манвэ:\r\n— У вас несчастные случаи в Валиноре были?\r\n— Нет, пока не было...\r\n— Будут!'),
(33, 'После того, как Саурон был повержен, Исилдур сказал Элронду:\r\n— Жаль, что я не захватил с собой веревку и мыло.\r\n— Зачем? — удивился Элронд.\r\n— Мыло — чтобы снять кольцо с пальца Саурона, а веревку — чтобы повесить его себе на шею!'),
(34, 'Арфинги: А Фелагунд смеется в Валиноре...\r\nНолфинги: И это хорошо! Хоть кто-то освободился от проклятья!\r\nФеаноринги: Над кем смеется? Над нами?'),
(35, 'Оптимус Прайм открыл окно. Из окна дуло. Оптимус Прайм закрыл окно, дуло исчезло, Мегатрон упал и разбился.'),
(36, '— А какого глаза нет у Магнуса — левого или правого?\r\n— Нарушит Никейский Эдикт — не будет обоих!'),
(37, 'Стебаться над Несущими Слово — одно удовольствие. А над Детьми Императора — другое!'),
(38, 'Мелисандра Джону Сноу: \r\n— После того, как тебя закололи и ты умер, где ты оказался? Что ты видел? \r\n— Видел толстого бородатого дядьку, который в сердцах сказал: «Достали твои поклонницы! Возвращайся обратно!».'),
(39, 'не вынес волдеморт провала\r\nи разлетелся на куски\r\nкогда вы фильм в тридэ смотрели\r\nв вас тоже есть его хоркрукс'),
(40, 'чего бы в книге не писали\r\nя точно знаю снейп уполз\r\nпусть роулинг напишет снарри\r\nфэндом ведь та еще змея '),
(41, 'я посмотрела на ебее\r\nпо тыще баксов стоит снейп\r\nда лучше за такие деньги\r\nя снейпа вылеплю сама'),
(42, 'в ночи один практичный дачник\r\nангбанда стены уволок\r\nа гондолин закрытый город\r\nего цивилы не нашли'),
(43, 'все в русской классике ужасно\r\nсовсем хороших нет людей\r\nребёнок книжку прочитает\r\nпойдёт старушек убивать'),
(44, 'ушел в отставку доктор хаус\r\nего заменит доктор кто\r\nбольного в тардис покатает\r\nи хвори снимет как рукой'),
(45, 'однажды эрион и фабий\r\nсовместный сделали проект\r\nкуда теперь пристроить орка\r\nсо щупальцами вместо ног'),
(46, 'нашел для опытов собаку\r\nоднажды доктор борменталь\r\nв квартире сириус прижился\r\nа швондер с криком отвалил'),
(47, 'однажды вместо айболита\r\nпоехал хаус в лимпопо\r\nтеперь спокойно могут дети\r\nпо жаркой африке гулять'),
(48, 'на помощь джекилу и хайду\r\nпришел отважный доктор веб\r\nон чистил джекила от хайда\r\nно вышло вдруг наоборот'),
(49, 'нашел однажды гамлет череп\r\nо бедный йорик говорит\r\nтут бэжэдэшники сбежались\r\nкричат за сколько продаешь'),
(50, 'люблю грозу в начале мая\r\nи в середине и в конце\r\nлюблю грозу в любое время\r\nкогда под крышею сижу'),
(51, 'однажды в хогвартс заявился\r\nс собакой вместе оберштайн\r\nсобаку тут же покормили\r\nа на него вниманья ноль'),
(52, 'однажды в хогвартс заявился\r\nискать кристаллы лорд кунсайт\r\nсбежал оттуда с громким криком\r\nнет не малфой я не малфой'),
(53, 'однажды в хогвартс заявился\r\nза чем то важным доктор кто\r\nотныне тардис в министерстве\r\nа доктор неизвестно где'),
(54, 'однажды в хогвартс заявился\r\nсам инквизитор эйзенхорн\r\nего вся школа убеждала\r\nчто дамблдор не еретик'),
(55, 'однажды в хогвартс заявился\r\nпривалов из ниичаво\r\nпросил у поттера совета\r\nкак выбегалло одолеть'),
(56, 'однажды в хогвартс заявилась\r\nстаруха злая шапокляк\r\nно после разговора с амбридж\r\nона решила доброй стать'),
(57, 'народная молва гласила\r\nчто нет на фенрисе волков\r\nно после третьей кружки эля\r\nони кругом кишмя кишат'),
(58, 'как в логге много персонажей\r\nкак тяжело их различать\r\nедва запомнила кирхайса\r\nи вот уж нет его в живых'),
(59, 'летит космический корабль\r\nпохож на сломанный утюг\r\nменя совсем не удивляет\r\nчто в космос не влезает он '),
(60, 'у оберштайна есть собака\r\nу яна вэнли крупный кот\r\nдруг с другом их бы познакомить\r\nглядишь и не было б войны'),
(61, 'обидел лоргара папаша\r\nжестоко веру растоптал\r\nв печали тот ушел в болото\r\nи там наелся демонят'),
(62, 'стремился магнус в гости к папе\r\nпоставил на уши весь варп\r\nпока проламывал барьеры\r\nзабыл о чем хотел сказать'),
(63, 'когда примарх у легиона\r\nдвоится у тебя в глазах\r\nне то ты слишком много выпил\r\nне то здесь альфа легион'),
(64, 'известно всем что на просперо\r\nесть твари жрущие мозги\r\nно руссу и волкам не страшно\r\nу них мозгов в помине нет'),
(65, 'как истинный знаток искусства\r\nфулгрим картины собирал\r\nно как то он перестарался\r\nкартина забрала его'),
(66, 'ангрон знакомился с астартес\r\nпочти без крови обошлось\r\nну уничтожил штук пятнадцать\r\nосталось больше все равно'),
(67, 'когда примарх расстроен чем то\r\nк нему не стоит подходить\r\nприкончит с одного удара\r\nсчитай отделался легко'),
(68, 'ушел на терру император\r\nоставив хорусу всю власть\r\nне любит и не доверяет\r\nдавно пора его убить'),
(69, 'спасти решил ангрона лоргар\r\nему почти что удалось\r\nну получился злобный демон\r\nзато живой и без гвоздей'),
(70, 'силен в теории робаут\r\nа в практике еще сильней\r\nв открытом космосе без шлема\r\nвсех превозмог назло врагам'),
(71, 'кого ты больше всех боишься\r\nспросил у дорна малкадор\r\nтебя сказал примарх угрюмо\r\nкогда советы мне даешь'),
(72, 'за что примарх ультрамаринов\r\nне любит альфа легион\r\nоднажды очень неудачно\r\nпытался их пересчитать'),
(73, 'альфарий я и ты альфарий\r\nи сто альфариев вокруг\r\nу нас в двадцатом легионе\r\nсплошное равенство царит'),
(74, 'сангвиния на сигнус прайме\r\nпытались демоны делить\r\nпока промеж собой ругались\r\nсангвиний взял и превозмог'),
(75, 'снабженцем быть у джагатая\r\nработы хуже не найти\r\nедва доставишь все припасы\r\nа он уже давно не здесь'),
(76, 'построить может пертурабо\r\nкрутой театр за пару дней\r\nно почему дворец свой папа\r\nдоверил дорну укреплять'),
(77, 'как жаль что папа император\r\nне сделал двадцать дочерей\r\nведь бурных девичьих разборок\r\nне выдержит и темный принц'),
(78, 'как мог простой лесной эльфийке\r\nдоверить сына трандуил\r\nа где другую взять невесту\r\nнет больше нолдорских принцесс'),
(79, 'в средневековье рисовали\r\nодин по библии фанарт\r\nпришла эпоха возрождения\r\nа с ней античности фандом'),
(80, 'сидели мирно на оффлайне\r\nи обсуждали новый фик\r\nкуда так быстро убежали\r\nвсе посетители кафе'),
(81, 'прочла подряд пятьсот фанфиков\r\nпотом засела за канон\r\nубогий слог тупые персы\r\nи не видать совсем энцы'),
(82, 'что делал мелькор в ангамандо\r\nпокуда не было войны\r\nбыть может вместе с сауроном\r\nпридумывал он чекэа'),
(83, 'все знают снейп уполз куда то\r\nа рокэ алва улетел\r\nсмеется финрод в валиноре\r\nон никуда не уползал'),
(84, 'я начиталась про бутлеги\r\nтеперь хочу бутлег себя\r\nпускай он ходит на работу\r\nа я все утро буду спать'),
(85, 'люблю весну в начале мая\r\nв конце июня не люблю\r\nа за весной в начале марта\r\nпридется ехать в краснодар'),
(86, 'не нужен нолдор бронепоезд\r\nне утащить его в исход\r\nизобрели бы броненосец\r\nтогда бы не было резни'),
(87, 'у врат менегрота внезапно\r\nлорд макалаурэ запел\r\nего заткнуть пытались братья\r\nи упустили сильмариль'),
(88, 'шел мирно фингон в альквалондэ\r\nи вдруг услышал наших бьют\r\nа разобраться кто тут наши\r\nне получилось в темноте'),
(89, 'я покупаю себе тело\r\nи продаю две пары глаз\r\nя не маньяк не извращенец\r\nя говорю о бэжэдэ'),
(90, 'не вынес волдеморт провала\r\nи разлетелся на куски\r\nкогда вы фильм в три дэ смотрели\r\nв вас тоже есть его хоркрукс'),
(91, 'я посмотрела на ебее\r\nпо тыще баксов стоит снейп\r\nда лучше за такие деньги\r\nя снейпа вылеплю сама');

